import React from "react";
import IdComponent from "../id-component/id-component"
import './main-component.css';
export default class MainComponent extends React.Component {
    constructor(){
        super();
        this.state = {
            UserDataComplete : [],
            UserDataIncomplete : [],
        }
    }
    componentDidMount(){
        fetch('https://randomuser.me/api/?page=1&results=100').then(
            res => res.json())
            .then((data) => {
                let finalData = [];
                for(let i = 0 ; i < data.length/2 ; ++i){
                    let temp = {};
                    temp.name = data.results[i].name;
                    temp.address = data.results[i].location.street.number + data.results[i].location.street.name + data.results[i].location.city + data.results[i].location.street.state;
                    temp.email = data.results[i].email;
                    temp.dob = data.results[i].dob;
                    temp.cellNumber = data.results.cell;
                    temp.picture =  data.results[i].picture.thumbnail;
                    finalData.push(temp);
                }
                console.log(finalData);
                this.setState({UserDataComplete : finalData});
                console.log(this.state.UserDataComplete[0]);
            })
    }
    render(){
        return(
            <IdComponent/>
        );
    }
}
