import React from "react";
import IdComponent from "../id-component/id-component"
import './main-component.css';
export default class MainComponent extends React.Component {
    constructor(){
        super();
        this.state = {
            UserDataComplete : [],
            UserDataIncomplete : [],
            currentPage : 1,
            firstPage : 1,
            lastPage,
            pageSize : 5
        }
        this.renderOb = this.renderOb.bind(this);
    }
    componentDidMount(){
        fetch('https://randomuser.me/api/?page=1&results=100').then(
            res => res.json())
            .then((data) => {
                let finalData = [];
                for(let i = 0 ; i < data.results.length/2 ; ++i){
                    let temp = {};
                    temp.name = data.results[i].name.title + " " + data.results[i].name.first + " " + data.results[i].name.last;
                    temp.address = data.results[i].location.street.number + " " + data.results[i].location.street.name + " " + data.results[i].location.city + " " + data.results[i].location.state;
                    temp.email = data.results[i].email;
                    temp.dob = data.results[i].dob.date;
                    temp.cellNumber = data.results[i].cell;
                    temp.picture =  data.results[i].picture.thumbnail;
                    finalData.push(temp);
                }
                this.setState({UserDataComplete : finalData});
                console.log(this.state.UserDataComplete[0]);
            });
    }
    renderOb =  () => {
        if(this.state.UserDataComplete[0]){
            let tempDatatoShow = [];
            let start = ((this.state.currentPage-1)*this.state.pageSize)+1;
            let end = start+this.state.pageSize;
            let lastPage = this.UserDataComplete.length/this.state.pageSize
            for(let i = start ; i < end ; ++i){
                tempDatatoShow.push(<IdComponent key={i} first={this.state.firstPage} last={lastPage} currentPage={this.state.currentPage} name={this.state.UserDataComplete[i].name} address={this.state.UserDataComplete[i].address} email={this.state.UserDataComplete[i].email} dob={this.state.UserDataComplete[i].dob} cellNumber={this.state.UserDataComplete[i].cellNumber} picture={this.state.UserDataComplete[i].picture} />)
            }
            return tempDatatoShow;
        }
            else {
                return (<div></div>)
            }
        }
    render(){
        return(
            <div>
                {this.renderOb()}
            </div>
        );
    }
}
