import React from "react";
import IdComponent from "../id-component/id-component"
import './main-component.css';
export default class MainComponent extends React.Component {
    constructor(){
        super();
        this.state = {
            UserDataComplete : [],
            UserDataIncomplete : [],
            currentPage : 1,
            firstPage : 1,
            pageSize : 2,
            lastpage : 0
        }
        this.renderOb = this.renderOb.bind(this);
        this.renderPag=this.renderPag.bind(this);
        this.paginationshift=this.paginationshift.bind(this);
        this.editData=this.editData.bind(this);
    }

    editData = (dat , index)=>{
        console.log(dat , index);
    }
    componentDidMount(){
        fetch('https://randomuser.me/api/?page=1&results=100').then(
            res => res.json())
            .then((data) => {
                let finalData = [];
                for(let i = 0 ; i < data.results.length/2 ; ++i){
                    let temp = {};
                    temp.name = data.results[i].name.title + " " + data.results[i].name.first + " " + data.results[i].name.last;
                    temp.address = data.results[i].location.street.number + " " + data.results[i].location.street.name + " " + data.results[i].location.city + " " + data.results[i].location.state;
                    temp.email = data.results[i].email;
                    temp.dob = data.results[i].dob.date;
                    temp.cellNumber = data.results[i].cell;
                    temp.picture =  data.results[i].picture.thumbnail;
                    finalData.push(temp);
                }
                this.setState({UserDataComplete : finalData});
                console.log(this.state.UserDataComplete[0]);
            });
    }
    renderOb =  () => {
        if(this.state.UserDataComplete[0]){
            let tempDatatoShow = [];
            let start = ((this.state.currentPage-1)*this.state.pageSize);
            let end = start+this.state.pageSize;
            let lastPage = this.state.UserDataComplete.length/this.state.pageSize
            for(let i = start ; i < end ; ++i){
                tempDatatoShow.push(<div><IdComponent key={i} index={i} editData={this.editData} first={this.state.firstPage} last={lastPage} currentPage={this.state.currentPage} name={this.state.UserDataComplete[i].name} address={this.state.UserDataComplete[i].address} email={this.state.UserDataComplete[i].email} dob={this.state.UserDataComplete[i].dob} cellNumber={this.state.UserDataComplete[i].cellNumber} picture={this.state.UserDataComplete[i].picture} /></div>)
            }
            return tempDatatoShow;
        }
            else {
                return (<div></div>)
            }
        }
    paginationshift = (dat)=>{
        if(dat == '-1'){
            let prevCurentPage = this.state.currentPage;
            this.setState({currentPage : prevCurentPage-1});
        }
        else if(dat == '+1'){
            let prevCurentPage = this.state.currentPage;
            this.setState({currentPage : prevCurentPage+1});
        }
        else if(dat == '1'){
            this.setState({currentPage : 1});
        }
        else if(dat == 'e'){
            let lastPage = this.state.UserDataComplete.length/this.state.pageSize;
            this.setState({currentPage : lastPage});
        }
    }
    renderPag = ()=>{
        this.state.lastpage = this.state.UserDataComplete.length/this.state.pageSize;
        return (
            <div className='pagination'>
                    <div onClick={() => {this.paginationshift('1')}} className="PagButton">First</div>
                    <div onClick={()=>{this.paginationshift('-1')}} className="PagButton"> Prev </div>
                    <div className="PagButton selected"> {this.state.currentPage} </div>
                    <div onClick={()=>{this.paginationshift('+1')}} className="PagButton"> Next </div>
                    <div onClick={()=>{this.paginationshift('e')}} className="PagButton">Last</div>
            </div>
        )
    }
    render(){
        return(
            <div>
                {this.renderOb()}
                {this.renderPag()}
            </div>
        );
    }
}
